import sailpoint.object.*;
import sailpoint.api.*;
import sailpoint.api.SailPointContext;
import sailpoint.object.Identity;
import sailpoint.object.ManagedAttribute;
import sailpoint.object.ProvisioningPlan;
import sailpoint.object.ProvisioningPlan.AccountRequest;
import sailpoint.object.ProvisioningPlan.AttributeRequest;
import sailpoint.workflow.WorkflowContext;

		String logPrefix = workflowLogPrefix + wfcontext.getStep().getName() + "::";
		cobLog(logPrefix, "EnterStep");

    	Identity ident=context.getObjectByName(Identity.class,identityName);  
        String appName="Dev_HDAP";
        String hdapNative =getNativeIdentity(idetityName,appName);


        //create HDAP account
        ProvisioningPlan.AccountRequest hdapAcctReq= new ProvisioningPlan.AccountRequest();
        hdapAcctReq.setOperation(sailpoint.object.ProvisioningPlan.AccountRequest.Operation.Modify);
        hdapAcctReq.setApplication(appName);
        hdapAcctReq.setNativeIdentity(hdapNative);


        if(changedAttrMap.containsKey("deptCode")){
        hdapAcctReq.add(new ProvisioningPlan.AttributeRequest("departmentNumber",changedAttrMap.get("deptCode")));
        }
        if(changedAttrMap.containsKey("displayName")){
        hdapAcctReq.add(new ProvisioningPlan.AttributeRequest("displayName",changedAttrMap.get("displayName")));
        }
        if(changedAttrMap.containsKey("firstName")){
        hdapAcctReq.add(new ProvisioningPlan.AttributeRequest("firstName",changedAttrMap.get("firstName")));
        }
        if(changedAttrMap.containsKey("lastName")){
        hdapAcctReq.add(new ProvisioningPlan.AttributeRequest("SN",changedAttrMap.get("lastName")));
        }

		ProvisioningPlan plan = new ProvisioningPlan();
        plan.setIdentity(ident);
		plan.add(hdapAcctReq);
		return plan;

